/*
 NOTES:
 1) Timing could be better.
 2) Gotta work on multicolour using BCM.
 3) Animations are next step and may require rewriting code depending on speed.
 */

int main() {


    //Activate clock for GPIO Port A, B, C, D, and F.
    SYSCTL_RCGC2_R |=  SYSCTL_RCGC2_GPIOF | SYSCTL_RCGC2_GPIOB  | SYSCTL_RCGC2_GPIOD | SYSCTL_RCGC2_GPIOE;

    //Set system clk speed to 40MHZ. Note: 50MHZ is too fast for the display.
    SYSCTL_RCC_R &= ~(SYSCTL_RCC_SYSDIV_M | SYSCTL_RCC_XTAL_M);
    SYSCTL_RCC_R |= (0x4 << 23); //Set SYSDIV to divide 200MHZ PLL by 5.
    SYSCTL_RCC_R |= SYSCTL_RCC_USESYSDIV;
    SYSCTL_RCC_R &= ~(SYSCTL_RCC_PWRDN | SYSCTL_RCC_BYPASS);
    SYSCTL_RCC_R |= SYSCTL_RCC_XTAL_16MHZ;
    SYSCTL_RCC_R &= ~(BIT4 | BIT5 | BIT0);

    GPIO_PORTF_DIR_R |= R1 | LATCH | CLK | OE;
    GPIO_PORTF_DEN_R |= R1 | LATCH | CLK | OE;

    GPIO_PORTD_DIR_R |= A | B | C | D | E;
    GPIO_PORTD_DEN_R |= A | B | C | D | E;

    GPIO_PORTB_DIR_R |= G1 | B1;
    GPIO_PORTB_DEN_R |= G1 | B1;

    GPIO_PORTE_DIR_R |= R2 | G2 | B2;
    GPIO_PORTE_DEN_R |= R2 | G2 | B2;

    GPIO_PORTF_DATA_R &= ~R1;
    GPIO_PORTD_DATA_R &= ~(A | B | C | D | E);
    GPIO_PORTB_DATA_R &= ~(G1 | B1);
    GPIO_PORTE_DATA_R &= ~(R2 | G2 | B2);


    while(1) {
        
        //Here i represents the rows. Since the matrix can display two rows at once then we only need to do 32 instead of 64.
        for (unsigned i = 0; i < 32; i++) {

//            msDelay(0.5);
            
            //Basically shift in data for the two rows needed. 
            //This indexing just goes through the array based on the current row. 
            //For example if i = 1 (aka row 2) then, we start at the j=64 and go until j=128.
            for (unsigned j = i * 64; j < 64 * (1+i); j++) {
                GPIO_PORTF_DATA_R &= ~CLK;

                GPIO_PORTF_DATA_R &= ~R1;
                GPIO_PORTB_DATA_R &= ~(G1 | B1);
                GPIO_PORTE_DATA_R &= ~(R2 | G2 | B2);

                if(colors[j] == 0x1f) {
//                    GPIO_PORTF_DATA_R |= R1;
                    GPIO_PORTB_DATA_R |= G1;
                    GPIO_PORTB_DATA_R |= B1;
                }

                else if (colors[j] == 0xe0){
                    GPIO_PORTF_DATA_R |= R1;
//                    GPIO_PORTB_DATA_R &= ~G1;
//                    GPIO_PORTB_DATA_R &= ~B1;
                }

                else if (colors[j] == 0xfc){
                    GPIO_PORTF_DATA_R |= R1;
                    GPIO_PORTB_DATA_R |= G1;
//                    GPIO_PORTB_DATA_R &= ~B1;
                }
                
                //The matrix displays a second row so we must jump to that corresponding "row" in the array.
                if(colors[j+2048] == 0x1f) {
//                    GPIO_PORTE_DATA_R |= R2;
                    GPIO_PORTE_DATA_R |= G2;
                    GPIO_PORTE_DATA_R |= B2;
                }

                else if (colors[j+2048] == 0xe0){
                    GPIO_PORTE_DATA_R |= R2;
//                    GPIO_PORTE_DATA_R &= ~G2;
//                    GPIO_PORTE_DATA_R &= ~B2;
                }

                else if (colors[j+2048] == 0xfc){
                    GPIO_PORTE_DATA_R |= R2;
                    GPIO_PORTE_DATA_R |= G2;
//                    GPIO_PORTE_DATA_R &= ~B2;
                }

                GPIO_PORTF_DATA_R |= CLK;

           }
           
           //Assert the blanking signal to blank screen
           GPIO_PORTF_DATA_R |= OE;
           
           //Sets the current row address
           //A,B,C,D are in PD0-PD3 but E is PD6 so the below must be used for correct operation.
           //Also note that moving these statements can lead to ghosting.
           GPIO_PORTD_DATA_R &= ~(A | B | C | D | E);
           GPIO_PORTD_DATA_R |= (i & 15) | ((i & BIT4) << 2);

           //Latch the data into the matrix
           GPIO_PORTF_DATA_R |= LATCH;
          
           //Deassert blanking signal and latch to display colors.
           GPIO_PORTF_DATA_R &= ~(OE | LATCH);

//           msDelay(0.8);

        }

    }
}

///////////////////////////////////////////////////////////////
// msDelay - Function to generate an approximate variable delay
// Arguments: ms - The number of milliseconds for the delay
// Return Value: None
//////////////////////////////////////////////////////////////
void msDelay(float ms) {

    //Convert input to loop iteration number
    ms *= ONE_MS;

    unsigned delay_cnt = (unsigned) ms;
    //Variable delay
    while (delay_cnt > 0) {

        delay_cnt--;
    }

}

unsigned extract_bits(unsigned val, unsigned num_bits, unsigned start_pos) {
    unsigned  mask = ((1 << num_bits) - 1) << start_pos;
    return (val & mask) >> start_pos;
}

void rgb_16(unsigned rgb, unsigned &r, unsigned &g, unsigned &b) {
    r = extract_bits(rgb, 5, 11);
    g = extract_bits(rgb, 6, 5);
    b = extract_bits(rgb, 5, 0);
}

